import React from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import Button from "react-bootstrap/Button";
import { ListGroup } from "react-bootstrap";
import { useHistory } from 'react-router-dom';

const Shoppies = () => {
  let movieDetails = null;

      function handleSubmitSearch(event) {
        event.preventDefault();
        let query = event.target.searchBar.value;
        query = query.replaceAll("'", "''");
        console.log('name string:', query);
        // searchRestaurantsByName(name);
        fetch(`http://www.omdbapi.com/?apikey=abaaeb92&t=${query}`)
        .then(response => response.json())
        .then(data => movieDetails = data)
        .then(console.log(movieDetails));
      }

  return (
    
    <Container
      className="sticky-top"
      
      fluid
    >
      <Row style={{ backgroundColor: "#2A9D8F" }}>
        <Col xs={2} md={1} className="align-self-end">
        </Col>

        <Col xs={2} md={2} className="align-self-end">

            <span
              style={{
                fontSize: "50px",
                fontFamily: "Open Sans",
                color: "white",
                WebkitTextStroke: "1px F8F8F8",
                textShadow: "0px 1px 4px #23430C"
              }}
            >The Shoppies</span>

        </Col>
        <Col md={8} className="align-self-center mt-2">

          <Form onSubmit={handleSubmitSearch}>
            <Form.Row className="align-items-center">
              <Col lg="8" className="m-auto">

                <InputGroup className="mb-2">

                  <FormControl
                    placeholder="Enter a Movie Title"
                    name="searchBar"
                  />

                  <InputGroup.Append>
                    <Button
                      variant="outline-secondary bg-light"
                      type="submit"
                      
                    >
                      Search
                  </Button>
                  </InputGroup.Append>
                </InputGroup>
              </Col>
            </Form.Row>
          </Form>
        </Col>
        
      </Row>
          <Container >
          <Row>
        <Col md={6} className="p-3 mt-5">
            <h3>Search Results</h3>
          <Container className="p-5 border" fluid>
          <Row></Row>
          
          </Container>
        </Col>

        <Col md={6} className="p-3 mt-5">
          <h3>Nominations</h3>
          <Container className="p-5 border" fluid>
            <Row>

            </Row>
          </Container>
        </Col>
      </Row>
          </Container>
    </Container>
  );
};

export default Shoppies;
