import React from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import Button from "react-bootstrap/Button";
//import axios from 'axios';
import { ListGroup } from "react-bootstrap";

const Shoppies = () => {

    const [search, searchValue] = React.useState('');
    
    
    const getMovie = async (search) => {
        fetch(`http://www.omdbapi.com/?i=tt3896198&apikey=abaaeb92&t=${search}`)
        .then((success) => { success.json() } )
        .then((movies) => { console.log(movies) } )
        .catch((error)=>{ console.log(error)
        })};

  return (
    <Container
      className="sticky-top"
      
      fluid
    >
      <Row style={{ backgroundColor: "#2A9D8F" }}>
        <Col xs={2} md={1} className="align-self-end">
        </Col>

        <Col xs={2} md={2} className="align-self-end">

            <span
              style={{
                fontSize: "50px",
                fontFamily: "Open Sans",
                color: "white",
                WebkitTextStroke: "1px F8F8F8",
                textShadow: "0px 1px 4px #23430C"
              }}
            >The Shoppies</span>

        </Col>
        <Col md={8} className="align-self-center mt-2">

          <Form>
            <Form.Row className="align-items-center">
              <Col lg="8" className="m-auto">

                <InputGroup className="mb-2">

                  <FormControl
                    placeholder="Enter a Movie Title"
                    pattern="^[A-Za-z0-9']*$"
                  />

                  <InputGroup.Append>
                    <Button
                      variant="outline-secondary bg-light"
                      type="submit"
                      onChange={(e => searchValue(e.target.value))}
                    >
                      Search
                  </Button>
                  </InputGroup.Append>
                </InputGroup>
              </Col>
            </Form.Row>
          </Form>
        </Col>
        
      </Row>
