import React from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import Button from "react-bootstrap/Button";

const Shoppies = () => {
  return (
    <Container style={{ backgroundColor: "#2A9D8F" }}>
      <Row>
        <Col xs={2} md={2} className="align-self-end">
          <span
            style={{
              fontSize: "50px",
              fontFamily: "Open Sans",
              color: "white",
              WebkitTextStroke: "1px F8F8F8",
              textShadow: "0px 1px 4px #23430C",
            }}
          >
            The Shoppies
          </span>
        </Col>
      </Row>
              {/* Search bar */}
              <Form>
            <Form.Row className="align-items-right">
              <Col lg="8" className="m-auto">
                {/* Label for screen readers only */}
                <Form.Label htmlFor="restaurantSearchBar" srOnly>
                  Enter a restaurant's name
                </Form.Label>

                <InputGroup className="mb-2">
                  {/* Restaurant search bar (must match alphanumeric and can contain apostrophes */}
                  <FormControl
                    name="restaurantSearchBar"
                    placeholder="Enter a restaurant's name"
                    maxLength="40"
                    pattern="^[A-Za-z0-9']*$"
                  />

                  {/* Submit search (button) */}
                  <InputGroup.Append>
                    <Button variant="outline-secondary bg-light" type="submit">
                      Go
                    </Button>
                  </InputGroup.Append>
                </InputGroup>
              </Col>
            </Form.Row>
          </Form>
    </Container>
  );
};

export default Shoppies;
