import React from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import Button from "react-bootstrap/Button";

const Shoppies = () => {
  const [movieDetails, setMovieDetails] = React.useState();
  const [itemList, setItemList] = React.useState();
  const [nominatedMovies, setNominatedMovies] = React.useState();

  function handleSubmitSearch(event) {
    event.preventDefault();
    let query = event.target.searchBar.value;
    query = query.replaceAll("'", "''");
    console.log("name string:", query);
    fetch(`http://www.omdbapi.com/?apikey=abaaeb92&s=${query}`)
      .then((response) => response.json())
      .then((data) => setMovieDetails(data));
  }

  React.useEffect(() => {
    console.log(nominatedMovies);

    const nominateMovie = (e) => {
      let newNominatedMovies = [nominatedMovies, e.target.value];
      setNominatedMovies(newNominatedMovies);
    }

    if (movieDetails) {
      const newitemList = movieDetails.Search.map((item, i) => {
        return (
          <Col
            className="border mb-4 mt-4"
            key={i}
            style={{
              fontFamily: "Georgia",
            }}
          >
            <h4 className="text-white mt-4">
              {`${item.Title}`}
              {", "}
              {`${item.Year}`}{" "}
              <Button variant="outline-dark text-dark" style={{backgroundColor:'#FFD700'}} onClick={nominateMovie}>
                Nominate
              </Button>
            </h4>

            <img src={`${item.Poster}`} alt=""></img>
          </Col>
        );
      });
      setItemList(newitemList);
    }
  }, [movieDetails, nominatedMovies]);

  return (
    <Container className="sticky-top" style={{backgroundColor:"#3b3e45"}} fluid>
      <Row style={{ backgroundColor: "black" }}>

        <Col xs={2} md={2} className="align-self-end">
          <span
            style={{
              fontSize: "50px",
              fontFamily: "Brush Script MT",
              color: "#FFD700",
            }}
          >
            The Shoppies
          </span>
        </Col>
        <Col md={8} className="align-self-center mt-2">
          <Form onSubmit={handleSubmitSearch}>
            <Form.Row className="align-items-center">
              <Col lg="8" className="m-auto">
                <InputGroup>
                  <FormControl
                    placeholder="Enter a Movie Title"
                    name="searchBar"
                  />

                  <InputGroup.Append>
                    <Button variant="outline-secondary bg-light" type="submit">
                      Search
                    </Button>
                  </InputGroup.Append>
                </InputGroup>
              </Col>
            </Form.Row>
          </Form>
        </Col>
      </Row>
      <Container>
        <Row>
          <Col md={6} className="p-3 mt-5" style={{color: "#FFD700"}}>
            <h1>Search Results</h1>
            <Container className="p-5 border" fluid>
              <Row>{itemList}</Row>
            </Container>
          </Col>

          <Col md={6} className="p-3 mt-5" style={{color: "#FFD700"}}>
            <h1>Nominations</h1>
            <Container className="p-5 border" fluid>
              <Row></Row>
            </Container>
          </Col>
        </Row>
      </Container>
    </Container>
  );
};

export default Shoppies;
